package LAB1;

public class CastingExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20;
		float b = (float)a;
		System.out.println(" The int to float Value is "+ b);
		int c = 125;
		byte x = (byte)c;
		System.out.println(" The int to byte value is "+ x );
		int z = 257;
		byte v = (byte)z;
		System.out.println(" The int to byte value is "+ v );
		float j = 24.52f;
		int f = (int)j;
		System.out.println(" The float to int value is "+ f );

	}

}
