package LAB1;

public class Division {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 20, b = 4; 
		int quotient = a / b; 
		int remainder = a % b; 
		System.out.println("Quotient = " + quotient); 
		System.out.println("Remainder = " + remainder);

	}

}
